import React from 'react';
import {
    Grid,
    Card,
    CardContent,
    Button,
    CardHeader,
    TextField,
    MenuItem,
    Typography
} from "@material-ui/core";
import { makeStyles } from '@material-ui/core/styles';
import CreateIcon from '@material-ui/icons/Create';



const hostels = [
    {
        value: 'H1',
        label: 'H1',
    },
    {
        value: 'H2',
        label: 'H2',
    },
    {
        value: 'H3',
        label: 'H3',
    },
    {
        value: 'H4',
        label: 'H4',
    },
];


const useStyles = makeStyles((theme) => ({
    form: {
        width: '100%', // Fix IE 11 issue.
        marginTop: theme.spacing(1),
    },
    card:{
        margin:'auto'
    }
}))


const AddNotice = () => {
    const classes = useStyles();
    return (
        <>
            <Grid container>

                <Grid item  xs={6} md="7" className={classes.card}>
                    <Card>
                        <CardContent>

                            <form className={classes.form}>

                                <TextField
                                    fullWidth
                                    label="Notice Title"
                                    icon="envelope"
                                    group
                                    type="text"
                                    validate
                                    error="wrong"
                                    success="right"
                                />

                                <TextField
                                    fullWidth
                                    label="Notice description"
                                    icon="lock"
                                    group
                                    type="text"
                                    validate
                                />
                                <TextField

                                    icon="lock"
                                    group
                                    type="Date"
                                    validate />

                                <TextField
                                    id="standard-select-currency"
                                    select
                                    label="Select"
                                    helperText="Please select the hostel"
                                    variant="standard"
                                >
                                    {hostels.map((option) => (
                                        <MenuItem key={option.value} value={option.value}>
                                            {option.label}
                                        </MenuItem>
                                    ))}
                                </TextField>

                                <Grid>
                                    <Button> 
                                        <Typography>Add Notice</Typography>
                                    </Button>
                                </Grid>





                            </form>

                        </CardContent>
                    </Card>
                </Grid>
            </Grid>
        </>
    );
};

export default AddNotice;
