import React from 'react';
import {
    Grid,
    Card,
    CardContent,
    Button,
    TextField,
    MenuItem,
    Typography
} from "@material-ui/core";
import { makeStyles } from '@material-ui/core/styles';
import { useHistory } from "react-router-dom";


const hostels = [
    {
        value: 'H1',
        label: 'H1',
    },
    {
        value: 'H2',
        label: 'H2',
    },
    {
        value: 'H3',
        label: 'H3',
    },
    {
        value: 'H4',
        label: 'H4',
    },
];


const useStyles = makeStyles((theme) => ({
    title:{
        color:'white',

    },
    form: {
        width: '100%', // Fix IE 11 issue.
        marginTop: theme.spacing(1),
        padding:'10px'
    },
    CardGrid: {
        margin: 'auto'

    },
    // Card: {
    //     marginTop: '5rem'
    // },
    calendar:{
       float:'left',
    },
    AddButton: {
        background: 'black',
        color: 'white',
        padding: '15px 15px 15px 15px',
        '&:hover': {
            background: "white",
            color: "black"
        },
        


    },
    ShowButton: {
        background: 'White',
        color: 'Black',
        padding: '15px 15px 15px 15px',
        marginTop: '2rem',
        '&:hover': {
            background: "Black",
            color: "white",
            border: "1px solid white",
        }
    },
}))


const AddNotice = () => {
    let history = useHistory();
    function noticeBoard(){
        history.push('/show');
    }
    const classes = useStyles();
    return (
        <>
            <Grid container>

                <Grid item xs={6} md={7} className={classes.CardGrid} >
                    <Typography variant="h1" className={classes.title}> NOTICE </Typography>
                    <Card className={classes.Card}>
                        <CardContent >

                            <form className={classes.form}>

                                <TextField
                                    fullWidth
                                    label="Notice Poster"
                                    group
                                    type="text"
                                    validate
                                    error="wrong"
                                    success="right"
                                />

                                <TextField
                                    fullWidth
                                    label="Notice Text"
                                    icon="lock"
                                    group
                                    type="text"
                                    validate
                                />
                                <TextField className={classes.calendar}

                                    icon="lock"
                                    group
                                    type="Date"
                                    validate />

                                <TextField className={classes.hostel}
                                    id="standard-select-currency"
                                    select

                                    helperText="Please select the hostel"
                                    variant="standard"
                                >
                                    {hostels.map((option) => (
                                        <MenuItem key={option.value} value={option.value}>
                                            {option.label}
                                        </MenuItem>
                                    ))}
                                </TextField>

                                <Grid>
                                    <Button variant="contained" className={classes.AddButton}>
                                        <Typography>Add Notice</Typography>
                                    </Button>
                                </Grid>





                            </form>

                        </CardContent>
                    </Card>
                </Grid>


            </Grid>
            <Grid>
                <Button variant="contained" className={classes.ShowButton} onClick={noticeBoard}>
                    <Typography>Show Notices</Typography>
                </Button>
            </Grid>
        </>
    );
};

export default AddNotice;
